INSERT INTO pois (id, name, description, type, address, rating, longitude, latitude) VALUES
(1, '安徽浮山国家地质公园', '安徽浮山国家地质公园以其独特的地质地貌和丰富的自然景观而闻名，园内分布着石灰岩地貌和溶洞群，是地质研究和自然探险的理想之地，游客可以观赏到惊险的地质构造，感受大自然的壮丽与神奇。', '自然景观', '浮山镇098县道旁', 5, 117.289070, 30.918007),
(2, '莲花湖公园', '莲花湖公园以湖光山色和文化活动闻名，湖泊清澈，四周环绕着绿意盎然的植被，是休闲观光的理想去处，公园内融入了莲花文化，每年莲花盛开时更是吸引众多游客前来欣赏。', '主题公园', '枞阳镇湖滨路23-6号', 3, 117.223591, 30.697346),
(3, '铜陵植物园', '铜陵植物园是一座集科普、观光和休闲为一体的综合性植物园，园内种植了大量珍稀植物和地方特色植物，游客可以欣赏到不同的花卉景观，了解植物知识，是全家出行和学生实地学习的理想场所。', '休闲娱乐', '北京中路975号', 3, 117.831018, 30.931123),
(4, '天井湖风景区', '天井湖风景区以其独特的环形山地貌和清澈的湖水闻名，周围群山环绕，景色壮丽，天井湖水质清新，适合划船、钓鱼等水上活动，周边植被茂密，是自然爱好者的天堂。', '主题公园', '长江西路1929号天井湖风景区内', 4, 117.799404, 30.941837),
(5, '凤凰山景区', '凤凰山景区集自然风光和人文历史于一体，景区内有独特的山水景观和丰富的革命历史遗迹，适合登山健身和红色旅游。', '自然景观', '顺安镇凤凰村', 4, 118.025895, 30.859415),
(6, '老洲太阳岛', '老洲太阳岛是一座生态湿地，位于长江中，拥有丰富的生物多样性和壮观的自然景观，岛上植被茂密，生态系统完好，适合城市居民远离喧嚣，感受大自然的宁静与美好。', '自然景观', '老洲乡', 3, 117.749341, 30.923709),
(7, '铜陵淡水豚自然保护区', '铜陵淡水豚自然保护区是中国唯一专门保护淡水豚的自然保护区，也是濒危物种保护的重要基地，保护区内保存了淡水豚及其栖息地的原始生态，研究人员和游客可以在这里观察和学习这些珍稀动物。', '休闲娱乐', '大通镇', 5, 117.739804, 30.808341),
(8, '铜陵市气象公园', '铜陵市气象公园是一座以气象为主题的科普教育基地，展示了丰富的气象知识和趣味性强的互动项目，游客可以了解天气、气候和自然现象的奥秘，是学生和家庭的理想游玩场所。', '主题公园', '西湖镇翠湖六路与天山大道交口北侧', 3, 117.850814, 30.970113),
(9, '铜陵大山生态旅游区', '铜陵大山生态旅游区以其原始的自然风光和独特的地貌著称，是登山、露营和生态旅游的好去处，景区内植被茂密，山水清丽，适合户外运动和自然探险。', '自然景观', '周潭镇大山村北', 4, 117.556897, 30.921366),
(10, '铜陵梅园', '铜陵梅园以其数千株梅花闻名，每年春季开放时更是吸引众多游客前来赏梅，梅园内环境优美，梅花品种丰富，适合拍照和休闲散步。', '主题公园', '001县道南侧', 2, 117.919022, 30.871178),
(11, '铜陵梧桐花谷景区', '铜陵梧桐花谷景区以其壮观的梧桐花海闻名，每年春夏之交，花谷赛霞，景色宜人，这里适合亲子游玩，拍照打卡，感受自然的美丽。', '休闲娱乐', '钟鸣镇梧桐花谷景区', 3, 118.148979, 30.945850),
(12, '渡江战役中线指挥部旧址', '渡江战役中线指挥部旧址是中国人民解放军在解放战争时期的重要历史遗址，具有深厚的历史文化价值，旧址保存完好，展示了解放战争时期的革命历史，是红色旅游的重要景点。', '文化场所', '正大街36号', 4, 117.210976, 30.693867),
(13, '西湖城市湿地公园', '西湖城市湿地公园是一座城市型湿地公园，集生态保护和休闲观光为一体，公园内湖泊清澈，绿化面积广阔，湿地生态系统完好，保护了许多动植物，还是城市居民放松身心的好去处。', '主题公园', '翠湖六路', 3, 117.859901, 30.972833),
(14, '白荡湖湿地公园', '白荡湖湿地公园以其丰富的生态资源和自然景观而闻名，是观鸟、摄影和休闲的理想去处，公园内环境幽静，湖水清澈，适合城市居民远离喧嚣，感受大自然的宁静与美好。', '主题公园', '会宫镇晓春村', 3, 117.346118, 30.838026),
(15, '凤凰山铜矿遗址', '凤凰山铜矿遗址是铜陵地区重要的工业遗产景点，展示了铜矿开采的历史和遗迹，游客可以在这里了解铜矿开采的历史，感受工业文化的独特魅力。', '文化场所', '006县道', 3, 118.035766, 30.853978),
(16, '大通古镇风景区', '大通古镇风景区是一座保存完好的古镇，古色古香，适合体验古镇文化和历史遗迹，古镇内有众多历史建筑和文化景点，游客可以感受浓厚的历史氛围。', '文化场所', '铜都大道南段8699号', 4, 117.732862, 30.814239),
(17, '左光斗纪念馆', '左光斗纪念馆是铜陵市重要的红色旅游景点，纪念左光斗同志的革命事迹，纪念馆内展示了左光斗的生平事迹和革命历史，是学习历史和红色教育的好去处。', '文化场所', '横埠镇', 4, 117.551222, 30.857315),
(18, '犁桥水镇', '犁桥水镇是一座具有江南水乡风格的生态小镇，适合休闲度假和体验水乡文化，小镇内河道纵横，水面倒映着白墙小屋，风景如画，游客可以在这里感受江南水乡的独特魅力，体验水上活动和当地文化。', '文化场所', '011县道南150米', 4, 117.887530, 31.029729),
(19, '三公山风景区', '三公山风景区以其独特的地貌和自然景观而闻名，是登山观景和生态旅游的理想去处，景区内山势险峻，植被茂密，景色壮丽，游客可以在这里登高望远，享受清新自然的空气和美景。', '自然景观', '鹿狮村', 3, 117.490621, 30.979323),
(20, '铜陵市动物园', '铜陵市动物园是一座城市型动物园，收藏了众多野生动物，适合家庭游玩和动物科普，动物园内环境优美，动物种类丰富，是城市居民近距离接触自然和学习动物知识的好去处。', '休闲娱乐', '天门镇长冲水库', 2, 117.856965, 30.809757),
(21, '枞阳方园', '枞阳方园是以方氏历史文化为主题的名人文化园，展示了方氏家族的历史和名人事迹，园内建筑古朴典雅，文化气息浓厚，适合文化爱好者和历史爱好者前来游览。', '文化场所', '义津镇高升村', 4, 117.165461, 30.945228),
(22, '刘大櫆故居', '刘大櫆故居是著名文学家刘大櫆的故居，展示了其生平事迹和民国时期的建筑风格，故居内陈设保持了当时的原貌，是文学爱好者和历史爱好者的理想去处。', '文化场所', '横埠镇周岗村刘家周庄', 4, 117.462531, 30.851649);